INSERT INTO `Email` (`ID`, `Email_Name`, `Email_Subject`, `Email_Body`) VALUES (1,'cut-email','[CVN] This is a cut email for {{event name}}','Hello {{usher name}},\r\n\r\nUnfortunately, we have fulfilled our usher quota for the {{event date}} performance of {{event name}}, and we will not be able to offer you an ushering position at this time.  Because we were unable to accommodate you, you have received 15 points to give you a better chance of being selected for the next show.\r\n\r\nHowever, you have been added to a waiting list, so there is still a chance you will be contacted to usher this event.  If you know you cannot make it, or you are no longer interested in ushering this event, please cancel on the website under \"My Events\".\r\n\r\nAny questions can be directed to the primary UC, {{event uc name}} at {{event uc}}\r\n\r\nThank you for your dedication to our club.'),(2,'confirmation-email','[CVN] Confirmed Usher for {{event name}}','Hello {{usher name}},\r\n\r\nYou have been selected to usher the {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nPlease check in with the usher coordinators (UCs)  {{meeting loc}} before {{event time}}.   Failure to arrive by the call time will result in a 5 point reduction, so please be on time!  Furthermore, if you are more than 15 minutes late, you will be sent home in addition to a 15 point deduction, per house policy.\r\n\r\n{{special instr}}\r\n\r\nRemember to come dressed in the proper ushering attire. \r\n\r\nProper ushering attire includes:\r\n\r\n-Nice blouses, cardigans or sweaters -- sleeves are a must!\r\n-Button up shirts or polos\r\n-Dress pants or slacks\r\n-Professional style, appropriate length skirts or dresses (w/sleeves)\r\n-Closed toe dress shoes\r\n\r\nYou will be asked to leave if you are wearing any of the following:\r\n\r\n-Tennis shoes or crocs\r\n-Fashion boots or weather specific boots\r\n-Leggings as pants\r\n-Jeans or denim of any kind (including black, grey or colored denim)\r\n-Tank top without a cover\r\n-Graphic t-shirts\r\n-Neon or loud patterns\r\n\r\nPlease feel free to email your UC if you have questions on dress code!!! :) \r\n\r\nIf you can\'t make it, you can cancel your request on the site under \"My Events\".  Canceling less than 48 hours before a show will result in a 15 point deduction.  If you know you can\'t make the show please cancel as soon as possible, so we can accommodate other ushers.\r\n\r\nAll other questions can be sent to your Primary UC, {{event uc name}} at {{event uc}}.\r\n\r\nThanks again for your help!'),(3,'replacement-email','[CVN] You have been selected as a replacement usher for {{event name}}','Hello {{usher name}},\r\n\r\nYou have been selected to usher the {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nPlease check in with the usher coordinators (UCs)  {{meeting loc}} before {{event time}}.   Failure to arrive by the call time will result in a 5 point reduction, so please be on time!  Furthermore, if you are more than 15 minutes late, you will be sent home in addition to a 15 point deduction, per house policy.\r\n\r\n{{special instr}}\r\n\r\nRemember to come dressed in the proper ushering attire. \r\n\r\nProper ushering attire includes:\r\n\r\n-Nice blouses, cardigans or sweaters -- sleeves are a must!\r\n-Button up shirts or polos\r\n-Dress pants or slacks\r\n-Professional style, appropriate length skirts or dresses (w/sleeves)\r\n-Closed toe dress shoes\r\n\r\nYou will be asked to leave if you are wearing any of the following:\r\n\r\n-Tennis shoes or crocs\r\n-Fashion boots or weather specific boots\r\n-Leggings as pants\r\n-Jeans or denim of any kind (including black, grey or colored denim)\r\n-Tank top without a cover\r\n-Graphic t-shirts\r\n-Neon or loud patterns\r\n\r\nPlease feel free to email your UC if you have questions on dress code!!! :) \r\n\r\nIf you can\'t make it, you can cancel your request on the site under \"My Events\".  Canceling less than 48 hours before a show will result in a 15 point deduction.  If you know you can\'t make the show please cancel as soon as possible, so we can accommodate other ushers.\r\n\r\nAll other questions can be sent to your Primary UC, {{event uc name}} at {{event uc}}.\r\n\r\nThanks again for your help!'),(4,'uc-email','[CVN] Reminder {{event name}}','Hello {{usher name}},\r\n\r\nThe final usher list has just been pulled for {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nUsher call time is {{event time}}.  Please arrive at least 15 minutes earlier than this for either Loeb or Elliott.\r\n\r\nThe Primary UC for this event is {{event uc name}} ({{event uc}}).\r\n\r\nRemember that, at this point, you will be held accountable for your status on the RSVP sheet.\r\n\r\nThanks!'),(6,'late-cut','There has been a late cancellation for {{event name}}','This email is to notify you that someone has canceled late from {{event name}}, and you may have a replacement usher show up who is not currently on your attendance list.\r\n\r\nThis is just for your information; no further action is necessary.\r\n\r\nThanks!'),(7,'no-replacement','There are no spots open for replacement for {{event name}}','Hello {{usher name}}, \r\n\r\nWe are sorry, but there are no spots open for a replacement for {{event name}}. \r\n\r\nThanks!\r\n{{event uc}}'),(5,'thank-you','[CVN] Thanks for ushering! Your attendance has been recorded! ','Hi {{usher name}}, \r\n\r\nThank you for signing up and ushering {{Dawn or Doom Keynote Speaker}}. Your attendance has been recorded. \r\n\r\nSign up for upcoming shows under \"My Events\" at http://web.ics.purdue.edu/~cvn/!\r\n\r\nYour point total is now {{Points}}.\r\n\r\nThanks!\r\n\r\n'),(8,'no-show','[CVN] Recorded No-Show for {{event name}}','Hello {{usher name}}\r\n\r\nWe missed you at the {{event date}} performance of {{event name}}.  Unfortunately, because you failed to cancel on the website, we had to penalize you 45 points.  If there has been a mistake or extenuating circumstances caused your absence please email {{event uc name}} at {{event uc}} so the situation can be corrected.\r\n\r\nYour point total is now {{Points}}. \r\n\r\nThank you,\r\nThe CVN E-Board'),(9,'two-day-replacement-email','[CVN] You have been selected as a replacement usher for {{event name}} ','Hello {{usher name}},\r\n\r\nYou have been selected as a short-notice replacement to usher the {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nSince you are receiving this notification on short notice, you will receive 15 points whether or not you are able to usher.  However, if you cannot make it, we would appreciate if you would cancel the show on the website under the \"My Events\" tab, so we can accommodate other ushers.\r\n\r\nIf you are able to usher, please check in with the usher coordinators {{meeting location}} by {{event time}}.  If you arrive more than 15 minutes late, we will have to send you home per house policy, so please be on time!\r\n\r\nRemember to come dressed in the proper ushering attire.  \r\n\r\nProper ushering attire include:\r\n\r\n-Nice blouses, cardigans or sweaters -- sleeves are a must!\r\n-Button up shirts or polos\r\n-Dress pants or slacks\r\n-Professional style, appropriate length skirts or dresses (w/sleeves)\r\n-Closed toe dress shoes\r\n\r\nYou will be asked to leave if you are wearing any of the following:\r\n\r\n-Tennis shoes or crocs\r\n-Fashion boots or weather specific boots\r\n-Leggings as pants\r\n-Jeans or denim of any kind (including black, grey or colored denim)\r\n-Tank tops without a cover\r\n-Graphic t-shirts\r\n-Neon or loud patterns\r\n\r\nFeel free to send any questions to the Primary UC {{event uc name}} at {{event uc}}.\r\n\r\nThanks again for your help!');
