INSERT INTO `cvn_email` (`id`, `name`, `description`, `subject`, `body`) VALUES (1,'cut-email','this is the cut email','This is a cut email for {{event name}} don\\\'t','Hello {{usher name}}, \r\n\r\nSorry, but you have been cut from the {{event date}}performance of {{event name}}.  You have been added to a wait list; there is still a chance you\\\'ll be contacted to usher this event.  If you know you can\\\'t make it, or you are no longer interested in ushering this event, you can cancel on the site under \\\"My Events\\\".\r\n\r\nThanks'),(2,'confirmation-email','this is the confirmation email','CVN: Confirmed Usher for {{event name}}','Hello {{usher name}},\r\n\r\nYou have been selected to usher the {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nPlease check in with the usher coordinators {{event uc}} at {{event name}} by {{event time}}. Remember to come dressed in the proper ushering attire.  (No jeans, t-shirts, sweatshirts, denim skirts, flip flops, or tennis shoes.)\r\n\r\nIf you can\'t make it you can cancel your request on the site under \"My Events\".  If you know you can\'t make the show please cancel as soon as possible.  \r\n\r\nAll other questions can be sent to your UC {{event uc}}.\r\n\r\nThanks again for your help!'),(3,'replacement-email','this is the replacement email','You have been selected as a replacement for {{even','Hello {{usher name}},\r\n\r\nYou have been selected to usher the {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\nPlease check in with the usher coordinators {{event uc}} at {{event name}} by {{event time}}. Remember to come dressed in the proper ushering attire.  (No jeans, t-shirts, sweatshirts, denim skirts, flip flops, or tennis shoes.)\r\n\r\nIf you can\'t make it you can cancel your request on the site under \"My Events\".  If you know you can\'t make the show please cancel as soon as possible.  \r\n\r\nAll other questions can be sent to your UC {{event uc}}.\r\n\r\nThanks again for your help!'),(4,'uc-email','this is the uc email','Reminder {{event name}}','Hello {{usher name}},\r\n\r\nThe final usher list has just been pulled for {{event date}} performance of {{event name}} at {{event place}}.\r\n\r\n\r\nThe UC for this event is {{event uc}}.\r\n\r\nThis is just a reminder in case you want to join us at the show!'),(6,'late-cut','this is the late cut email','There has been a late cancellation for {{event nam','This email is to notify you that someone has canceled late from {{event name}}. \r\n\r\nYou now have the option to go back on the website, look at the cut list, and then the request list, and see if you would like to offer anyone else the chance to usher {{event name}}. \r\n\r\nIf so, please email them manually and not through the system. \r\n\r\nThanks!'),(7,'no-replacement','this is the no replacement email','There are no spots open for replacement for {{even','Hello {{usher name}}, \r\n\r\nWe are sorry, but there are no spots open for replacement for {{event name}}. \r\n\r\nThanks!\r\n{{event uc}}'),(5,'thank-you','this is the thank you email','CVN Thanks for ushering! Your attendance has been ','Hi {{usher name}}, \r\nThank you for signing up and ushering {{event name}}. Your attendance has been recorded. \r\n\r\nSign up for upcoming shows! http://web.ics.purdue.edu/~cvn/\r\n\r\n\r\nThanks!\r\n\r\n');
